'use strict';

/**
 * @file say-hi.test - Test the card removal functionality
 */

import * as test from 'tape';
import sayHi from '../lib/say-hi';

test('Test sayHi function', function sayHiTest(t) {
	let greeting = sayHi('Ada Lovelace');
	t.equal(greeting, 'Hello, Ada Lovelace!', 'Greeting should say "Hello, Ada Lovelace!"');
	t.end();
});
